mapper.insert:
INSERT INTO REACT_MEMBER (
    MEM_ID
    , MEM_NAME
    , MEM_PW
    , MEM_TEL
    , MEM_INTRO
) VALUES (
    #{memId}
    , #{memName}
    , #{memPw}
    , #{memTel}
    , #{memIntro}
)

mapper.update:
UPDATE REACT_MEMBER
SET
    MEM_NAME = #{memName}
    ,MEM_PW = #{memPw}
    ,MEM_TEL = #{memTel}
    ,MEM_INTRO = #{memIntro}
WHERE MEM_ID = #{memId}
