mapper.insert:
INSERT INTO SHOP_ITEM (
    ITEM_NAME
    , ITEM_PRICE
    , ITEM_INTRO
) VALUES (
    #{itemName}
    , #{itemPrice}
    , #{itemIntro}
)

mapper.update:
UPDATE SHOP_ITEM
SET
    ITEM_NAME = #{itemName}
    , ITEM_PRICE = #{itemPrice}
    , ITEM_INTRO = #{itemIntro}
WHERE ITEM_CODE = #{itemCode}
